// Home.js
import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
    return (
        <div className="dark:bg-theme-dark-background text-theme-light-text dark:text-theme-dark-text h-screen w-full overflow-hidden flex flex-col justify-between">
            <div className="flex justify-between items-center p-12">
                <div className="text-4xl font-bold">Home</div>
                <button className="bg-gray-200 dark:bg-gray-700 p-4 rounded-lg">Settings</button>
            </div>
            <div className='p-12 flex-grow'>
                <div className="grid grid-cols-6 grid-rows-2 gap-4 w-full" style={{height: 'calc(100vh - 3rem - 3rem - 3rem - 3rem - 3rem - 3rem)'}}> 
                    {/* Bento boxes */}
                </div>
            </div>
            <div className="flex justify-between items-center p-12">
                <button className="bg-gray-200 dark:bg-gray-700 p-4 rounded-lg">Speech to Text</button>
                <div className="flex-grow"></div> {/* This ensures the search bar is centered */}
                <input type="text" placeholder="Search..." className="p-4 rounded-lg" />
                <div className="flex-grow"></div> {/* This ensures the search bar is centered */}
            </div>
        </div>
    );
};

export default Home;
