// Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import BentoBoxes from '../components/BentoBoxes';

const Home = () => {
    return (
        <div className="dark:bg-theme-dark-background text-theme-light-text dark:text-theme-dark-text h-screen w-full overflow-hidden flex flex-col justify-between">
            <div className="flex justify-between items-center pt-12 pl-12 pr-12">
                <div className="text-4xl font-bold">Home</div>
                <button className="bg-gray-200 dark:bg-gray-700 p-4 rounded-lg border border-white">Settings</button>
            </div>
            <div className='p-12 flex-grow'>
                <BentoBoxes />
            </div>
            <div className="flex items-center p-12">
                <button className="bg-gray-200 dark:bg-gray-700 p-4 rounded-lg">Speech to Text</button>
                <div className="flex-grow"></div> {/* This ensures the search bar is centered */}
                <div className="bg-gray-200 dark:bg-gray-700 p-4 rounded-lg w-1/3">Search Bar</div>
                <div className="flex-grow"></div> {/* This ensures the search bar is centered */}
            </div>
        </div>
    );
};

export default Home;
